DROP TABLE IF EXISTS `STUDENT_CHAPTER`;

CREATE TABLE STUDENT_CHAPTER(
	SID INT,
	CHID INT,
	ABILITY DOUBLE(20,10) DEFAULT 0,
	DISCRIMINATION DOUBLE(20,10) DEFAULT 0,
	DIFFICULTY DOUBLE(20,10) DEFAULT 0,
    NUMBER_OF_ATTEMPTS INT DEFAULT 0,
    NUMBER_OF_HINTS INT DEFAULT 0,
    NUMBER_OF_INCORRECT_ATTEMPTS INT DEFAULT 0,
	ENROLL_YEAR DATE,
	FOREIGN KEY (SID) REFERENCES STUDENT(SID),
	FOREIGN KEY (CHID) REFERENCES CHAPTER(CHID)
);

