import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ScrollingCarousel } from '@trendyol-js/react-carousel';
import { FiBook } from 'react-icons/fi';
import { Header, IRT } from '../components';

import { GridComponent, ColumnDirective, ColumnsDirective, Page, Filter, Group, Inject, Sort } from '@syncfusion/ej2-react-grids';
import { ListViewComponent } from '@syncfusion/ej2-react-lists';

import { chapterData,studentData,moduleData } from '../data/dummy';


const Instructors = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/student_progress/?SID=2&BID=2');
        setData(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);
  console.log(data);
  const [book, setBook] = useState('');

  const filteredData = data.filter((data) => data.BOOK === book);

  const customHeader = () => {
    return (
      <span className='e-headertext'>Students</span>
    );
  }
  const customHeader1 = () => {
    return (
      <span className='e-headertext'>Chapters</span>
    );
  }
  const customHeader2 = () => {
    return (
      <span className='e-headertext'>Modules</span>
    );
  }

  function handleSelect(e) {
    console.log("Selected item:", e.text);
  }


  return (
    <div className='m-2 md:m-10 mt-24 p-2 md:p-10 bg-[#F6F6F6] rounded-3xl'>
      <Header category="Page" title="Home" />

      <div className='m-5 text-[40px] text-[#066579] font-bold'>Your Books</div>
      <div>
        <ScrollingCarousel show={3.5} slide={3} swiping={true} >
          <button className='text-[25px] bg-white m-6 p-3 h-[100px] w-[300px] hover:drop-shadow-xl rounded-3xl font-bold' onClick={() => setBook('Introduction to Programming')}><FiBook className='inline mr-' />Introduction to Programming</button>
          <button className='text-[25px] bg-white m-6 p-3 h-[100px] w-[300px] hover:drop-shadow-xl rounded-3xl font-bold' onClick={() => setBook('Database Systems')}>Database Systems</button>
          <button className='text-[25px] bg-white m-6 p-3 h-[100px] w-[300px] hover:drop-shadow-xl rounded-3xl font-bold' onClick={() => setBook('Computer Networks')}>Computer Networks</button>
          <button className='text-[25px] bg-white m-6 p-3 h-[100px] w-[300px] hover:drop-shadow-xl rounded-3xl font-bold' onClick={() => setBook('Web Development')}>Web Development</button>
          <button className='text-[25px] bg-white m-6 p-3 h-[100px] w-[300px] hover:drop-shadow-xl rounded-3xl font-bold' onClick={() => setBook('Operating Systems')}>Operating Systems</button>
          <button className='text-[25px] bg-white m-6 p-3 h-[100px] w-[300px] hover:drop-shadow-xl rounded-3xl font-bold' onClick={() => setBook('Data Structures and Algorithms')}>Data Structures and Algorithms</button>
          <button className='text-[25px] bg-white m-6 p-3 h-[100px] w-[300px] hover:drop-shadow-xl rounded-3xl font-bold' onClick={() => setBook('Computer Graphics')}>Computer Graphics</button>
          <button className='text-[25px] bg-white m-6 p-3 h-[100px] w-[300px] hover:drop-shadow-xl rounded-3xl font-bold' onClick={() => setBook('Software Engineering')}>Software Engineering</button>
        </ScrollingCarousel>
      </div>

      <div className='m-5 text-[40px] text-[#066579] font-bold'>Student Preformance</div>

      <div>
        {/* <IRT/> */}
      </div>
      <div style={{ display: "flex" }}>
        <ListViewComponent id="StudentList" dataSource={studentData} fields={{ id: 'id', text: 'name' }} width={300} height={500}
          showHeader={true} headerTemplate={customHeader} select={handleSelect} style={{ marginRight: "25px", marginLeft: "200px" }}
        >
        </ListViewComponent>

        <ListViewComponent id="ChapterList" dataSource={chapterData} fields={{ id: 'id', text: 'chapter' }} width={300} height={500}
          showHeader={true} headerTemplate={customHeader1} select={handleSelect} style={{ marginRight: "25px" }}
        >
        </ListViewComponent>

        <ListViewComponent id="ModuleList" dataSource={moduleData} fields={{ id: 'id', text: 'name' }} width={300} height={500}
          showHeader={true} headerTemplate={customHeader2} select={handleSelect}
        >
        </ListViewComponent>
      </div>
      <div style={{ margin: '10%', marginTop: '5%' }}>
        <GridComponent dataSource={studentData} allowPaging={true} pageSettings={{ pageSize: 10 }} allowFiltering={true} allowGrouping={true} allowSorting={true}>
          <ColumnsDirective>
            <ColumnDirective field='name' headerText='Name' textAlign='Left' width={100} />
            <ColumnDirective field='timespent' headerText='Time_Spent' textAlign='Left' width={100} />
            <ColumnDirective field='incorrect' headerText='Incorrect' textAlign='Left' width={100} />
            <ColumnDirective field='hints' headerText='Hints Taken' textAlign='Left' width={100} />
          </ColumnsDirective>
          <Inject services={[Page, Filter, Group, Sort]} />
        </GridComponent>
      </div>

    </div>
  );
};
export default Instructors;
