import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Plot from 'react-plotly.js';
import { create, all } from 'mathjs';
import Papa from 'papaparse';

const math = create(all);

function IRT() {
  const [result, setResult] = useState(null);
  const [plotData, setPlotData] = useState({ icc: { x: [], y: [] }, tcc: { x: [], y: [] }});
  const [selectedExercise, setSelectedExercise] = useState(0);
  const [discrimination, setDiscrimination] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    const data = 'attempts_test.csv';
    axios
      .post(`http://localhost:4000/estimate/${data}`)
      .then(response => {
        setDiscrimination(response.data.Discrimination);
        updatePlotData(selectedExercise, response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleExerciseChange = (event) => {
    const exerciseIndex = parseInt(event.target.value);
    setSelectedExercise(exerciseIndex);
    updatePlotData(exerciseIndex);
  };

  const updatePlotData = (exerciseIndex, data) => {
    const { Ability, Discrimination, Difficulty } = data;
    const x = Ability || [];
    const a = Discrimination || [];
    const b = Difficulty || [];
    const selectedDiscrimination = a[exerciseIndex];
    const selectedDifficulty = b[exerciseIndex];

    const sigmoid = (x, a, b) => {
      const denominator = 1 + Math.exp(-a * (x - b));
      return denominator === 0 ? NaN : 1 / denominator;
    };

    const y = x.map((val) => sigmoid(val, selectedDiscrimination, selectedDifficulty));
    const sortedData = x.map((value, index) => [value, y[index]]).sort((a, b) => a[0] - b[0]);
    const sortedX = sortedData.map(pair => pair[0]);
    const sortedY = sortedData.map(pair => pair[1]);

    const meanA = math.mean(a);
    const meanB = math.mean(b);
    const minAbility = math.min(...Ability);
    const maxAbility = math.max(...Ability);
    const range = maxAbility - minAbility;
    const step = range / 100;
    const abilityValues = math.range(minAbility, maxAbility, step).toArray();
    const y2 = abilityValues.map((val) => sigmoid(val, meanA, meanB));

    setPlotData({
      icc: { x: sortedX, y: sortedY },
      tcc: { x: abilityValues, y: y2 }
    });
  };

  return (
    <div>
      <select value={selectedExercise} onChange={handleExerciseChange}>
        {discrimination.map((_, index) => (
          <option key={index} value={index}>
            Exercise {index}
          </option>
        ))}
      </select>
    <div style={{ display: 'flex' }}>
      <div style={{ flex: 1 }}>
        <div>Item Response Curve</div>
        <Plot
          data={[
            {
              x: plotData.icc.x,
              y: plotData.icc.y,
              type: 'scatter',
              mode: 'lines+markers',
              line: { color: 'black' },
              marker: { symbol: 'circle', size: 8 }
            }
          ]}
          layout={{
            width: 550,
            height: 600,
            title: 'IRT Model - Item Response Curve',
            xaxis: { title: 'Latent Trait' },
            yaxis: { title: 'Item Response' }
          }}
        />
      </div>
      <div style={{ flex: 1 }}>
        <div>Test Characteristic Curve</div>
        <Plot
          data={[
            {
              x: plotData.tcc.x,
              y: plotData.tcc.y,
              type: 'scatter',
              mode: 'lines',
              line: { color: 'black' },
              marker: { symbol: 'circle', size: 8 }
            },
          ]}
          layout={{ width: 550, height: 600, title: 'IRT Model - Test Characteristic Curve' }}
        />
      </div>
    </div>
  </div>
);

}

export default IRT;
