import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Plot from 'react-plotly.js';
import { create, all } from 'mathjs';
import Papa from 'papaparse';

const math = create(all);

function IRT() {
  const [result, setResult] = useState(null);
  const [plotData, setPlotData] = useState({ icc: { x: [], y: [] }, tcc: { x: [], y: [] } });
  const [selectedExercise, setSelectedExercise] = useState(0);
  const [exerciseOptions, setExerciseOptions] = useState([]);

  const handleClick = () => {    
    const data = 'attemps_test.csv'; // Replace this with the actual file name
    axios
    .post(`http://localhost:4000/estimate/${data}`)
      .then(response => {
        const exercises = response.data;
        const numExercises = exercises.length;
        const options = Array.from(Array(numExercises).keys()); // Generate an array of numbers from 0 to numExercises-1
        setExerciseOptions(options);

        const selectedExerciseData = exercises[selectedExercise];
        const x = selectedExerciseData.Ability ?? [];
        const a = selectedExerciseData.Discrimination ?? [];
        const b = selectedExerciseData.Difficulty ?? [];
        const sigmoid = (x, a, b) => {
          const denominator = 1 + math.exp(-a * (x - b));
          return denominator === 0 ? NaN : 1 / denominator;
        };
        const y = x.map((val, index) => sigmoid(val, a[index], b[index]));
        const sortedData = x.map((value, index) => [value, y[index]]).sort((a, b) => a[0] - b[0]);
        const sortedX = sortedData.map(pair => pair[0]);
        const sortedY = sortedData.map(pair => pair[1]);

        const meanA = math.mean(a);
        const meanB = math.mean(b);
        const minAbility = math.min(...x);
        const maxAbility = math.max(...x);
        const range = maxAbility - minAbility;
        const step = range / 100;
        const abilityValues = math.range(minAbility, maxAbility, step).toArray();
        const y2 = abilityValues.map((val) => sigmoid(val, meanA, meanB));

        setPlotData({
          icc: { x: sortedX, y: sortedY },
          tcc: { x: abilityValues, y: y2 }
        });
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleExerciseChange = (event) => {
    setSelectedExercise(parseInt(event.target.value));
  };

  return (
    <div>
      <div>
        <select onChange={handleExerciseChange}>
          {exerciseOptions.map(exercise => (
            <option key={exercise} value={exercise}>{`Exercise ${exercise + 1}`}</option>
          ))}
        </select>
        <button onClick={handleClick}>Estimate</button>
      </div>
      <div style={{ display: 'flex' }}>
        <div style={{ flex: 1 }}>
          <div>Item Response Curve</div>
          <Plot
            data={[
              {
                x: plotData.icc.x,
                y: plotData.icc.y,
                type: 'scatter',
                mode: 'lines+markers',
                line: { color: 'black' },
                marker: { symbol: 'circle', size: 8 }
              }
            ]}
            layout={{
              width: 550,
              height: 600,
              title: 'IRT Model - Item Response Curve',
              xaxis: { title: 'Latent Trait' },
              yaxis: { title: 'Item Response' }
            }}
          />
        </div>
        <div style={{ flex: 1 }}>
          <div>Test Characteristic Curve</div>
          <Plot
            data={[
              {
                x: plotData.tcc.x,
                y: plotData.tcc.y,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'black' },
                marker: { symbol: 'circle', size: 8 }
              },
            ]}
            layout={{ width: 550, height: 600, title: 'IRT Model - Test Characteristic Curve' }}
          />
        </div>
      </div>
    </div>
  );
}

export default IRT;
